import { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import { DialogTitle, DialogContent } from '@mui/material';
import axios from 'axios';

import CreateMeeting from './meetings/createMeeting';

const IndivGroup = (props)=>{
    const group = props.group

    const [meetings,setMeetings] = useState(group.meetings)
    const [open,setOpen] = useState(false)

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    const createMeetingsDisplay = ()=>{
        return meetings.map(meeting=>{
            var config = {
                method:"get",
                url:"http://localhost:5000/api/meetings/getMeeting",
                headers:{
                    "Content-type":"application/json"
                },
                params:{
                    meetingId:meeting
                }
            }
            axios(config)
            .then(res=>{
                const meetingInfo = res.data.meeting
                return(
                    <div key={meeting}>
                        <p>{meetingInfo.name}</p>
                        <p>{meetingInfo.range}</p>
                        <p>{meetingInfo.duration}</p>
                    </div>
                )
            })
            .catch(err=>{
                console.log(err)
            })
        })
    }

    useEffect(()=>{
        getMeetingInfo()
    },[])

    return(
        <div>
            <button variant="outlined" onClick={handleClickOpen}>
                View More
            </button>
            <Dialog
                open={open} 
                onClose={handleClose}
                sx={{
                    "& .MuiDialog-container": {
                      "& .MuiPaper-root": {
                        width: "100%",
                        height: "100%",
                        maxWidth: "90vw",
                      },
                    },
                  }}
            >
                <DialogTitle>{group.name}</DialogTitle>
                <DialogContent>
                    <p>Meetings:</p>
                    <CreateMeeting group={group._id} rerender={setMeetings}/>
                    {createMeetingsDisplay()}
                </DialogContent>
            </Dialog>
        </div>
    )
}

export default IndivGroup
