import { useState, useRef } from 'react';
import Dialog from '@mui/material/Dialog';
import { DialogTitle, DialogContent } from '@mui/material';
import axios from 'axios';

const CreateMeeting = ()=>{
    const [open,setOpen] = useState(false)
    const ref = useRef(null)

    const [meetingName,setMeetingName] = useState("")
    const [meetingLength,setMeetingLength] = useState(0)
    const [memberField,setMemberField] = useState("")
    const [members,setMembers] = useState([])

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setMeetingName("")
        setMeetingLength(0)
        ref.current.value=""
        setMembers([])
        setOpen(false);
    };

    const handleAddMember = ()=>{
        setMembers(prevMembers=>{
            return [...prevMembers,memberField]
        })
        ref.current.value=""
    }

    const removeAddedMember = (removeMember)=>{
        setMembers(prevMembers=>{
            const newMembers = [...prevMembers]
            newMembers.filter(member=>member===removeMember)
            return newMembers
        })
    }

    const handleCreateMeeting = ()=>{

    }

    const addedMembersDisplay = ()=>{
        return members.map(member=>{
            return(
                <div key={member}>
                    {member}
                    <button onClick={()=>{removeAddedMember(member)}}>X</button>
                </div>
            )
        })
    }
    return(
        <div>
            <button variant="outlined" onClick={handleClickOpen}>
                Create New Meeting
            </button>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Create New Meeting</DialogTitle>
                <DialogContent>
                    <form>
                        <label>Name:
                            <input onChange={(e)=> setMeetingName(e.target.value)}></input>
                        </label><br></br>
                        <label>Length:
                            <input type="number" onChange={(e)=> setMeetingLength(e.target.value)}></input>
                        </label><br></br>
                        <label>Add Members:
                            <input
                                ref={ref}
                                onChange={(e)=>setMemberField(e.target.value)}
                                onKeyDown={(e)=>{
                                    e.key==="Enter" && handleAddMember()
                                }}
                            ></input>
                        </label><br></br>
                        {addedMembersDisplay()}
                        <button type='button' onClick={handleCreateMeeting}>Create</button>
                    </form>
                </DialogContent>
            </Dialog>
        </div>
    )
}

export default CreateMeeting
