import { useState } from "react"

import "./body.css"

const Body=()=>{
    const [centerCardDisplay,setCenterCardDisplay] = useState(1)
    const cards = [
        {
            front:"Step 1: Getting Started",
            back:"Create an account with us and join/create a group"
        },
        {
            front:"Step 2: Scheduleing a new meeting",
            back:"Create a new meeting. Enter a length and date range for the meeting"
        },
        {
            front:"Step 3: Inviting your group members",
            back:"Invite your group members and wait for them to upload their availabilities"
        },
        {
            front:"Step 4: Creating a schedule",
            back:"Once everyone finishes uploading, our algorithm will generate which slot best fit the group's schedule"
        }
    ]

    const changeSlide=(num)=>{
        setCenterCardDisplay(prevCenterCardDisplay=>prevCenterCardDisplay+num)
    }

    return(
        <div className="about-page">
            <div className="about-page-howTo">
                <h1>How Does LetsMeet Work?</h1>
                <div className="about-page-howTo-cardsections">
                    <button onClick={()=>{changeSlide(-1)}} className="about-page-howTo-cardsections-button">{"<"}</button>
                    <div className="about-page-howTo-cardsections-card">
                        <h2>{cards[centerCardDisplay-1].front}</h2>
                        <p>{cards[centerCardDisplay-1].back}</p>
                    </div>
                    <div className="about-page-howTo-cardsections-card">
                        <h2>{cards[centerCardDisplay].front}</h2>
                        <p>{cards[centerCardDisplay].back}</p>
                    </div>
                    <div className="about-page-howTo-cardsections-card">
                        <h2>{cards[centerCardDisplay+1].front}</h2>
                        <p>{cards[centerCardDisplay+1].back}</p>
                    </div>
                    <button onClick={()=>{changeSlide(1)}} className="about-page-howTo-cardsections-button">{">"}</button>
                </div>
            </div>
        </div>
    )
}

export default Body
