        const usersList = uploaded ? props.meeting.haveUploaded : props.meeting.haveNotUploaded
        return usersList.map(user=>{
            const email = user.user ? user.user : user
            return(
                <div key={email}>
                    {email}
                </div>
            )
        })
    }

    const slotSubmissionDisplay=()=>{
