    const startDate = new Date(props.meeting.range[0].split("T")[0].replaceAll('-','/'))
    const endDate = new Date(props.meeting.range[1].split("T")[0].replaceAll('-','/'))
    const numDays = (Math.ceil((endDate.getTime()-startDate.getTime())/(1000*3600*24)))+1
    const [slots,setSlots] = useState(Array.from({length: 48},()=> Array.from({length: numDays}, () => false)))

    const createSubmissionDisplay=()=>{
        return slots.map(row=>{
            return (
                <div className='groups-page-content-main-current-individualDisplay-full-section-indiv-full-content1-content-schedule-row'>
                    {row.map(col=>{
                        return <div className='groups-page-content-main-current-individualDisplay-full-section-indiv-full-content1-content-schedule-row-block'>Hi</div>
                    })}
                </div>
            )
        })
    }

    const createRangeVisual=()=>{
